import React from 'react';
import {
  Button,
  Input,
  Footer,
  Card,
  CardBody,
  CardImage,
  CardTitle,
  CardText
} from "mdbreact";
import './App.css';

class App extends React.Component {
  state = { apiResponse: []
   }

callAPI() {
    fetch("http://localhost:9000/testAPI")
        .then(res => res.json())
        .then(apiResponse => this.setState({apiResponse}));
}
onchange = e => {
  this.setState({ search: e.target.value });
};
componentDidMount() {
    this.callAPI();
}
  render(){
  return (
    <div className="App">
         <div className="col">
                <Input
                  label="Search Product"
                  icon="search"
                  onChange={this.onchange}
                />
              </div>
        {/* <p className="App-intro">{this.state.apiResponse}</p> */}
          <ul>
            {this.state.apiResponse.map(apidata =>
              <li key={apidata.id}>{apidata.Name} {apidata.Image} <br/>
              {apidata.Category} <br/> {apidata.Price}
                </li>
              )}
          </ul>
    </div>
  );
}
}
export default App;
