import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  state = { apiResponse: [] }

callAPI() {
    fetch("http://localhost:9000/testAPI")
        .then(res => res.json())
        .then(apiResponse => this.setState({apiResponse}));
}

componentDidMount() {
    this.callAPI();
}
  render(){
  return (
    <div className="App">

        {/* <p className="App-intro">{this.state.apiResponse}</p> */}
          <ul>
            {this.state.apiResponse.map(apidata =>
              <li key={apidata.id}>{apidata.Name} {apidata.Image}
              {}
                </li>
              )}
          </ul>
    </div>
  );
}
}
export default App;
