import React from 'react';
import {
  Button,
  Input,
  Footer,
  Card,
  CardBody,
  CardImage,
  CardTitle,
  CardText
} from "mdbreact";
import './App.css';

class App extends React.Component {
  state = { apiResponse: [],
    search: ""
   }

callAPI() {
    fetch("http://localhost:9000/testAPI")
        .then(res => res.json())
        .then(apiResponse => this.setState({apiResponse}));
}
productList = product => {
  return (
    <div className="col-md-3" style={{ marginTop: "20px" }}>
      <Card>
        <CardBody>
            <CardTitle title={product.Name}>
            {product.Name}
            </CardTitle>
        </CardBody>
      </Card>
    </div>
  );
};
onchange = e => {
  this.setState({ search: e.target.value });
};
componentDidMount() {
    this.callAPI();
}
  render(){
    const { search } = this.state;
    // const filteredCountries = countriesList.filter(country => {
    //   return country.name.toLowerCase().indexOf(search.toLowerCase()) !== -1;
    // });
  return (
    <div className="App">
         <div className="col">
                <Input
                  label="Search Product"
                  icon="search"
                  onChange={this.onchange}
                />
              </div>
        {/* <p className="App-intro">{this.state.apiResponse}</p> */}
          {/* <ul>
            {this.state.apiResponse.map(apidata =>
              <li key={apidata.id}>{apidata.Name} {apidata.Image} <br/>
              {apidata.Category} <br/> {apidata.Price}
                </li>
              )}
          </ul> */}
           <div className="row">
              {this.state.apiResponse.map(product => {
                return this.productList(product);
                console.log(this.productList(product));
              })}
            </div>
    </div>
  );
}
}
export default App;
